import * as _ from "lodash"
import { AnnouncementJson } from "../types";

export class Announcement {
  id: number
  title: string
  content: string
  link: string
  locale: string
  createdAt: Date
  updatedAt: Date

  static fromJSON(json: AnnouncementJson): Announcement {
    const announcement = new Announcement

    announcement.id = json.id
    announcement.title = json.title
    announcement.content = json.content
    announcement.link = json.link
    announcement.locale = json.locale
    announcement.createdAt = new Date(json.createdAt)
    announcement.updatedAt = new Date(json.updatedAt)

    return announcement
  }
}