import { CommonTransactionJson } from "../types"

export class CommonTransaction {
  id: string
  type: string
  account: string
  data: object
  status: string
  createdAt: Date

  static fromJSON(commonTransactionJson: CommonTransactionJson): CommonTransaction {
    const tx = new CommonTransaction()
    tx.id = commonTransactionJson.id
    tx.type = commonTransactionJson.type
    tx.status = commonTransactionJson.status
    tx.account = commonTransactionJson.account
    tx.data = commonTransactionJson.data
    tx.createdAt = new Date(commonTransactionJson.createdAt)
    return tx
  }
}